import { useState} from "react";

function App() {
  const[counte,setCounte]=useState(0)
  const[sevalue,setValue]=useState(0)
  function changeSelectVal(e){
    setValue(e.target.value)
  }

  function increment(){
    setCounte(counte+sevalue*1)
  }
  return (
<div>
  <h2>当前求和为：{counte}</h2>
  <select value={sevalue} onChange={changeSelectVal}>
    <option value={1}>1</option>
    <option value={2}>2</option>
    <option value={3}>3</option>
    <option value={4}>4</option>
  </select> {'\u00A0'}
  <button onClick={increment}>加1</button>{'\u00A0'}
  <button>减1</button>{'\u00A0'}
  <button>奇数1</button>{'\u00A0'}
  <button>异步1</button>{'\u00A0'}
</div>
  );
}

export default App;
