import { createSlice,nanoid } from "@reduxjs/toolkit"; 
const initialState = [{id:"1",date:"2022-12-3",time:"19:00",cont:"玩游戏"},
{id:"2",date:"2022-12-4",time:"16:00",cont:"学习"},
{id:"3",date:"2022-12-5",time:"12:00",cont:"看戏"}]
const todoSlice=createSlice(
    {
        name:"todolist",
        initialState,
        reducers:{
            addlist:{
                reduce(state,action){
                    console.log(action)
                //  state.push({id:nanoid(),date:"2022-12-4",time:"16:00",cont:action.payload})
                },
                prepare(name,date,time){
                return{
                    payload: {
                        id: nanoid(),
                        date,
                        time,
                        cont:name
                }
            }}},
            dellist:(state)=>{
                state.length=0
            }
        }        
    })

export const {addlist,dellist}= todoSlice.actions;
export default todoSlice.reducer;
