import { useState } from "react"
import { useSelector } from "react-redux"
import { useDispatch } from "react-redux"
import { addlist,dellist } from "./todoSlice"
function Todo (){
     const[name,setName]=useState()
     const dispatch=useDispatch()
     const todolist=useSelector((state)=>{return state.addlist})
     const addListCont=()=>{
        dispatch(addlist(name))
        setName("")
     }
     const delListCont=(id)=>{
        dispatch(dellist(id))
        console.log("aaa")
     }
    return(
        <div>
         <input value={name} onChange={(e)=>{setName(e.target.value)}}/>
         <button onClick={addListCont}>增加</button>
         <div>
         {todolist.map(item=>(<ul key={item.id}>{item.cont}<button onClick={delListCont(item.id)}>删除</button></ul>))}
         </div>
        </div>
    )
}
export default Todo
