import { createSlice } from "@reduxjs/toolkit"; 

const sliceCounter=createSlice(
    {
        name:"counter",
        initialState:{
            count:0
        },
        reducers:{
            add:(state)=>{
                state.count=state.count+1
            },
            jian:(state)=>{
                state.count=state.count-1
            }
        }
    }
)
export const {add,jian}= sliceCounter.actions;
export default sliceCounter.reducer;
