import { createSlice } from "@reduxjs/toolkit"; 
// import { nanoid } from 'nanoid'

const todoSlice=createSlice(
    {
        name:"todolist",
        initialState:{
            list:[{id:1,name:"玩游戏",done:false},{id:2,name:"学习",done:false},{id:3,name:"逛街",done:false}]
        },
        reducers:{
            addlist:(state)=>{
                state.list=[{id:4,name:"123",done:false},{id:5,name:"234",done:false}]
                // {id:nanoid(),name:action.payload,done:false}
            }
        }        
    })
export const {addlist}= todoSlice.actions;
export default todoSlice.reducer;
