import { createSlice } from "@reduxjs/toolkit"; 
import { nanoid } from "@reduxjs/toolkit"
const initialState = [{id:"1",cont:"玩游戏"},
{id:"2",cont:"学习"},
{id:"3",cont:"看戏"}]
const todoSlice=createSlice(
    {
        name:"todolist",
        initialState,
        reducers:{
            addlist:(state,action)=>{
                state.push({id:nanoid(),cont:action.payload})
            },
            dellist:(state)=>{
              state=[]
            }
        }        
    })
export const {addlist,dellist}= todoSlice.actions;
export default todoSlice.reducer;
