import { useState} from "react";

function App() {
  const[counte,setCounte]=useState(0)
  const[sevalue,setValue]=useState(1)
  function changeSelectVal(e){
    setValue(e.target.value)
  }

  function increment(){
    setCounte(counte+sevalue*1)
  }
  function decrement(){
    setCounte(counte-sevalue*1)
  }
  function ifcrement(){
    if (counte % 2!==0){
      setCounte(counte+sevalue*1)
    }   
  }
  function addcrement(){
    setTimeout(() => {
      setCounte(counte+sevalue*1)
    }, 2000);
  }

  return (
<div>
  <h2>当前求和为：{counte}</h2>
  <select value={sevalue} onChange={changeSelectVal}>
    <option value={1}>1</option>
    <option value={2}>2</option>
    <option value={3}>3</option>
    <option value={4}>4</option>
  </select> {'\u00A0'}
  <button onClick={increment}>加</button>{'\u00A0'}
  <button onClick={decrement}>减</button>{'\u00A0'}
  <button onClick={ifcrement}>奇数加</button>{'\u00A0'}
  <button onClick={addcrement}>异步加</button>{'\u00A0'}
</div>
  );
}

export default App;
